import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable, of } from 'rxjs';
import { switchMap, catchError } from 'rxjs/operators';
import { PerfilService } from './perfil.service';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private cedulaSubject = new BehaviorSubject<string | null>(null);

  constructor(private perfilService: PerfilService) {}

  setCedula(cedula: string) {
    this.cedulaSubject.next(cedula);
  }

  clear() {
    this.cedulaSubject.next(null);
  }

  getCedula(): string | null {
    return this.cedulaSubject.value;
  }

  // Exponer un observable que consulta el perfil en la base de datos cada vez que cambia la cédula
  perfil$(): Observable<any | null> {
    return this.cedulaSubject.pipe(
      switchMap(cedula => {
        if (!cedula) {
          return of(null);
        }
        return this.perfilService.consultarPerfilPorCedula(cedula).pipe(
          catchError(() => of(null))
        );
      })
    );
  }
}

import { Component } from '@angular/core';
import { RouterModule, Router } from '@angular/router';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-dashboard',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './dashboard.html',
  styleUrl: './dashboard.css'
})
export class DashboardComponent {
  perfil: any = {}; // Inicializa vacío

  constructor(private router: Router) {
    // Simulamos la carga desde BD o desde tu servicio de login
    this.cargarPerfil();
  }

  cargarPerfil() {
    // Esto debe venir de tu servicio que hace login
    // Aquí solo un ejemplo temporal
    this.perfil = {
      nombre: 'Admin',
      rol: 'admin',
      gestionarEmpleados: true
    };
  }

  verMiPerfil() {
    this.router.navigate(['/mi-perfil']);
  }

  registrarPerfil() {
    this.router.navigate(['/registrar-perfil']);
  }

  gestionarEmpleados() {
    this.router.navigate(['/gestionar-empleados']);
  }

  gestionarTarjetas() {
    this.router.navigate(['/tarjetas']);
  }

  gestionarVentas() {
    this.router.navigate(['/ventas']);
  }

  logout() {
    // Lógica real de logout
    console.log('Cerrando sesión');
    this.router.navigate(['/login']);
  }

  registrarIncidencia(): void {
    this.router.navigate(['/registrar-incidencia']);
  }
  consultarIncidencia(): void {
    this.router.navigate(['/consultar-incidencia']);
  }
}

import { Component, OnDestroy } from '@angular/core';
import { RouterModule, Router } from '@angular/router';
import { CommonModule } from '@angular/common';
import { PerfilService } from '../core/services/perfil.service';
import { AuthService } from '../core/services/auth.service';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-dashboard',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './dashboard.html',
  styleUrl: './dashboard.css'
})
export class DashboardComponent implements OnDestroy {
  perfil: any = null; // Inicializa a null
  private sub: Subscription | null = null;

  constructor(private router: Router, private perfilService: PerfilService, private authService: AuthService) {
    // Dejar la carga para ngOnInit
  }

  ngOnInit() {
    // Suscribirse al perfil desde AuthService (que consulta la BD)
    this.sub = this.authService.perfil$().subscribe(perfil => {
      this.perfil = perfil;
      if (!this.perfil) {
        this.router.navigate(['/login']);
      }
    });
  }

  ngOnDestroy() {
    this.sub?.unsubscribe();
  }

  verMiPerfil() {
    this.router.navigate(['/mi-perfil']);
  }

  registrarPerfil() {
    this.router.navigate(['/registrar-perfil']);
  }

  gestionarEmpleados() {
    this.router.navigate(['/gestionar-empleados']);
  }

  gestionarTarjetas() {
    this.router.navigate(['/tarjetas']);
  }

  gestionarVentas() {
    this.router.navigate(['/ventas']);
  }

  logout() {
    // Limpiar state de auth
    this.authService.clear();
    console.log('Cerrando sesión');
    this.router.navigate(['/login']);
  }

  registrarIncidencia(): void {
    this.router.navigate(['/registrar-incidencia']);
  }
  consultarIncidencia(): void {
    this.router.navigate(['/consultar-incidencia']);
  }
}
